// StockGPT.jsx - With Stop-Loss Functionality
import React, { useState, useEffect, useRef } from 'react';
import { 
  FaChartLine, FaRobot, FaSearch, FaArrowUp, FaArrowDown, 
  FaStar, FaPlug, FaDatabase, FaExclamationTriangle, FaShieldAlt 
} from 'react-icons/fa';
import { MagnifyingGlassIcon } from '@heroicons/react/24/outline';
import './styles/App.css';
import './styles/Index.css';


const StockGPT = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [stockData, setStockData] = useState(null);
  const [stopLossOrders, setStopLossOrders] = useState([]);
  const [warnings, setWarnings] = useState([]);
  const [stopLossConfig, setStopLossConfig] = useState({
    enabled: true,
    risk_percentage: 2.0,
    trailing_stop: false,
    trailing_percentage: 5.0
  });

  const messagesEndRef = useRef(null);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleSend = async () => {
    if (!input.trim()) return;
    
    const userMessage = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setLoading(true);
    
    try {
      const response = await fetch('http://localhost:8000/api/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          query: input,
          stop_loss_config: stopLossConfig
        }),
      });
      
      const data = await response.json();
      
      setMessages(prev => [...prev, { 
        role: 'assistant', 
        content: data.analysis,
        stockData: data.stock_data
      }]);
      
      if (data.stock_data) {
        setStockData(data.stock_data);
      }
      
      if (data.stop_loss_orders) {
        setStopLossOrders(data.stop_loss_orders);
      }
      
      if (data.warnings) {
        setWarnings(data.warnings);
      }
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [...prev, { 
        role: 'assistant', 
        content: 'Sorry, I encountered an error processing your request.'
      }]);
    }
    
    setLoading(false);
    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  const handleStopLossConfigChange = (field, value) => {
    setStopLossConfig(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handlePlaceManualStopLoss = async () => {
    if (!stockData) return;
    
    try {
      const response = await fetch('http://localhost:8000/api/place-stop-loss', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          symbol: stockData.symbol,
          stop_price: stockData.latestPrice * (1 - stopLossConfig.risk_percentage / 100),
          quantity: 10, // Default quantity
          trailing: stopLossConfig.trailing_stop,
          trailing_percentage: stopLossConfig.trailing_percentage
        })
      });
      
      const result = await response.json();
      
      if (result.success) {
        setStopLossOrders(prev => [...prev, result]);
        setMessages(prev => [...prev, {
          role: 'assistant',
          content: `Stop-loss order placed successfully. Order ID: ${result.order_id}`
        }]);
      } else {
        setWarnings(prev => [...prev, `Failed to place order: ${result.error}`]);
      }
    } catch (error) {
      console.error('Error placing stop-loss:', error);
      setWarnings(prev => [...prev, 'Error placing stop-loss order']);
    }
  };

  const suggestedQueries = [
    "Analyze AAPL and set stop-loss before earnings",
    "TSLA technical analysis with auto stop-loss",
    "MSFT quarterly results protection",
    "NVDA stop-loss setup for high volatility",
    "Protect my AMZN position before event"
  ];

  return (
    <div className="stockgpt-container">
      {/* <header className="stockgpt-header">
        <div className="logo">
          <FaChartLine className="logo-icon" />
          <h1>StockGPT</h1>
        </div>
        <p>AI-powered stock analysis with automated stop-loss protection</p>
      </header> */}

      <div className="stockgpt-layout">
        <div className="chat-container">
          {messages.length === 0 ? (
            <div className="welcome-message">
              <div className="welcome-icon">
                <FaShieldAlt />
              </div>
              <h2>Automated Stop-Loss Protection</h2>
              <p>Get AI analysis and automatically protect your positions with stop-loss orders</p>
              
              <div className="stop-loss-config">
                <h3>Stop-Loss Configuration</h3>
                <div className="config-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={stopLossConfig.enabled}
                      onChange={(e) => handleStopLossConfigChange('enabled', e.target.checked)}
                    />
                    Enable Auto Stop-Loss
                  </label>
                </div>
                
                <div className="config-item">
                  <label>Risk Percentage:</label>
                  <input
                    type="range"
                    min="0.5"
                    max="10"
                    step="0.5"
                    value={stopLossConfig.risk_percentage}
                    onChange={(e) => handleStopLossConfigChange('risk_percentage', parseFloat(e.target.value))}
                  />
                  <span>{stopLossConfig.risk_percentage}%</span>
                </div>
                
                <div className="config-item">
                  <label>
                    <input
                      type="checkbox"
                      checked={stopLossConfig.trailing_stop}
                      onChange={(e) => handleStopLossConfigChange('trailing_stop', e.target.checked)}
                    />
                    Trailing Stop
                  </label>
                </div>
                
                {stopLossConfig.trailing_stop && (
                  <div className="config-item">
                    <label>Trailing Percentage:</label>
                    <input
                      type="range"
                      min="1"
                      max="20"
                      step="0.5"
                      value={stopLossConfig.trailing_percentage}
                      onChange={(e) => handleStopLossConfigChange('trailing_percentage', parseFloat(e.target.value))}
                    />
                    <span>{stopLossConfig.trailing_percentage}%</span>
                  </div>
                )}
              </div>
              
              <div className="suggested-queries">
                <h3>Try asking:</h3>
                {suggestedQueries.map((query, index) => (
                  <div 
                    key={index} 
                    className="suggestion-card"
                    onClick={() => setInput(query)}
                  >
                    <FaSearch className="suggestion-icon" />
                    <span>{query}</span>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div className="messages-container">
              {messages.map((message, index) => (
                <div key={index} className={`message ${message.role}`}>
                  <div className="message-content">
                    {message.role === 'assistant' && (
                      <div className="assistant-avatar">
                        <FaRobot />
                      </div>
                    )}
                    <div className="message-text">
                      {message.content}
                      {message.stockData && (
                        <div className="stock-data-preview">
                          <h4>{message.stockData.symbol} - {message.stockData.companyName}</h4>
                          <div className="price-info">
                            <span className={`price ${message.stockData.change >= 0 ? 'up' : 'down'}`}>
                              ${message.stockData.latestPrice}
                            </span>
                            <span className={`change ${message.stockData.change >= 0 ? 'up' : 'down'}`}>
                              {message.stockData.change >= 0 ? <FaArrowUp /> : <FaArrowDown />}
                              {Math.abs(message.stockData.change)} ({Math.abs(message.stockData.changePercent)}%)
                            </span>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              ))}
              
              {loading && (
                <div className="message assistant">
                  <div className="message-content">
                    <div className="assistant-avatar">
                      <FaRobot />
                    </div>
                    <div className="message-text">
                      <div className="typing-indicator">
                        <span></span>
                        <span></span>
                        <span></span>
                      </div>
                    </div>
                  </div>
                </div>
              )}
              <div ref={messagesEndRef} />
            </div>
          )}

          <div className="input-container">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask about any stock or request stop-loss protection..."
              disabled={loading}
            />
            <button onClick={handleSend} disabled={loading}>
              <FaArrowUp />
            </button>
          </div>
        </div>

        <div className="sidebar">
          {stockData ? (
            <div className="stock-details">
              <h2>{stockData.symbol}</h2>
              <h3>{stockData.companyName}</h3>
              
              <div className="price-section">
                <div className={`current-price ${stockData.change >= 0 ? 'up' : 'down'}`}>
                  ${stockData.latestPrice}
                </div>
                <div className={`price-change ${stockData.change >= 0 ? 'up' : 'down'}`}>
                  {stockData.change >= 0 ? <FaArrowUp /> : <FaArrowDown />}
                  {Math.abs(stockData.change)} ({Math.abs(stockData.changePercent)}%)
                </div>
              </div>
              
              <div className="action-section">
                <h3>
                  <FaShieldAlt /> Stop-Loss Protection
                </h3>
                
                <div className="stop-loss-info">
                  <div className="info-item">
                    <span>Recommended Stop Price:</span>
                    <span>${(stockData.latestPrice * (1 - stopLossConfig.risk_percentage / 100)).toFixed(2)}</span>
                  </div>
                  <div className="info-item">
                    <span>Risk Amount:</span>
                    <span>${(stockData.latestPrice * (stopLossConfig.risk_percentage / 100)).toFixed(2)}</span>
                  </div>
                  <div className="info-item">
                    <span>Risk Percentage:</span>
                    <span>{stopLossConfig.risk_percentage}%</span>
                  </div>
                </div>
                
                <button 
                  className="btn-stop-loss"
                  onClick={handlePlaceManualStopLoss}
                  disabled={!stopLossConfig.enabled}
                >
                  <FaShieldAlt /> Place Stop-Loss Order
                </button>
              </div>
              
              {stopLossOrders.length > 0 && (
                <div className="orders-section">
                  <h3>Placed Orders</h3>
                  {stopLossOrders.map((order, index) => (
                    <div key={index} className="order-card">
                      <div className="order-header">
                        <span className="order-id">{order.order_id}</span>
                        <span className={`order-status ${order.status.toLowerCase()}`}>
                          {order.status}
                        </span>
                      </div>
                      <div className="order-details">
                        <div>Symbol: {order.symbol}</div>
                        <div>Type: {order.order_type}</div>
                        <div>Side: {order.side}</div>
                        <div>Quantity: {order.quantity}</div>
                        {order.stop_price && <div>Stop Price: ${order.stop_price}</div>}
                      </div>
                    </div>
                  ))}
                </div>
              )}
              
              {warnings.length > 0 && (
                <div className="warnings-section">
                  <h3>
                    <FaExclamationTriangle /> Warnings
                  </h3>
                  {warnings.map((warning, index) => (
                    <div key={index} className="warning-message">
                      {warning}
                    </div>
                  ))}
                </div>
              )}
            </div>
          ) : (
            <div className="sidebar-placeholder">
              <FaChartLine className="placeholder-icon" />
              <p>Stock data will appear here after analysis</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default StockGPT;
